import React, { useState } from 'react'
import { Text, Button } from '@rneui/themed'
import { View, FlatList } from 'react-native'


export default function Matching ({ route }) {
  const { triviaName, triviaItems } = route.params;
  const [selectedOptions, setSelectedOptions] = useState({});

  const handleOptionPress = (prompt, option) => {
    setSelectedOptions((prevSelectedOptions) => ({
      ...prevSelectedOptions,
      [prompt]: option,
    }));
  };

  const handleCheckAnswers = () => {
    const isAllMatched = triviaItems.every((item) =>
      item.answer.every((option) => selectedOptions[option.prompt] === option.value)
    );
    if (isAllMatched) {
      alert('All items are matched!');
    } else {
      alert('Not all items are matched.');
    }
  };

  const renderItem = ({ item }) => (
    <View>
      <Text>{item.prompt}</Text>
      {item.answer.map((option, index) => (
        <View key={index}>
          <Button onPress={() => handleItemPress(option)}>
            <Text>{`${option.option}: ${option.value}`}</Text>
          </Button>
        </View>
      ))}
    </View>
  )

  return (
    <View>
      <Text>{triviaName}</Text>
      <FlatList
        data={triviaItems}
        renderItem={renderItem}
        keyExtractor={(item) => item.prompt}
      />
      <Button onPress={handleCheckAnswers}>
        <Text>Check Answers</Text>
      </Button>
    </View>
  );
};
