import React, { useState } from 'react'
import { Text, Button } from '@rneui/themed'
import { View, FlatList, TouchableOpacity } from 'react-native'


export default function Matching({ route }) {
  const { triviaName, triviaItems } = route.params;

  const [matchedItems, setMatchedItems] = useState([]);

  const handleMatch = (option, value, index) => {
    const matchedItem = { option, value, index };
    setMatchedItems([...matchedItems, matchedItem]);
  };

  const handleCheckAnswers = () => {
    const isAllMatched = matchedItems.length === triviaItems.length;
    if (isAllMatched) {
      const isAllCorrect = matchedItems.every(
        (item) =>
          triviaItems.find((t) => t.option === item.option && t.value === item.value)
      );
      const result = {
        matchedItems: matchedItems,
        isAllCorrect: isAllCorrect
      };
      navigation.navigate('Summary', { result });
    } else {
      alert('Not all items are matched.');
    }
  };

  const renderItem = ({ item, index }) => (
    <View style={{ marginBottom: 16 }}>
      <Text style={{ fontSize: 20, fontWeight: 'bold', marginBottom: 8 }}>
        {item.prompt}
      </Text>
      <View style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
        <View style={{ flex: 1, marginRight: 8 }}>
          {item.answer.map((option, answerIndex) => {
            const matchedItem = matchedItems.find(
              (matchedItem) =>
                matchedItem.option === option.option && matchedItem.value === null
            );
            const isSelected = matchedItem?.index === index && matchedItem?.value === null;
            return (
              <TouchableOpacity
                key={`${item.prompt}_${option.option}`}
                style={{
                  backgroundColor: isSelected ? 'orange' : 'white',
                  borderWidth: 1,
                  borderColor: 'black',
                  padding: 16,
                  marginBottom: 8,
                  borderRadius: 8
                }}
                onPress={() => handleMatch(option.option, null, index)}>
                <Text style={{ textAlign: 'center' }}>{option.option}</Text>
              </TouchableOpacity>
            );
          })}
        </View>
        <View style={{ flex: 1, marginLeft: 8 }}>
          {item.answer.map((value, answerIndex) => {
            const matchedItem = matchedItems.find(
              (matchedItem) =>
                matchedItem.option === null && matchedItem.value === value.value
            );
            const isSelected = matchedItem?.index === index && matchedItem?.option === null;
            return (
              <TouchableOpacity
                key={`${item.prompt}_${value.value}`}
                style={{
                  backgroundColor: isSelected ? 'orange' : 'white',
                  borderWidth: 1,
                  borderColor: 'black',
                  padding: 16,
                  marginBottom: 8,
                  borderRadius: 8
                }}
                onPress={() => handleMatch(null, value.value, index)}>
                <Text style={{ textAlign: 'center' }}>{value.value}</Text>
              </TouchableOpacity>
            );
          })}
        </View>
      </View>
    </View>
  );

  return (
    <View style={{ padding: 16 }}>
      <Text style={{ fontSize: 24, fontWeight: 'bold', marginBottom: 16 }}>{triviaName}</Text>
      <FlatList data={triviaItems} renderItem={renderItem} keyExtractor={(item) => item.prompt} />
      <View style={{ marginTop: 16 }}>
        <Button title="Check Answers" onPress={handleCheckAnswers} />
      </View>
    </View>
  );
}
