import React, { useState } from 'react'
import { Text, Button } from '@rneui/themed'
import { View, FlatList } from 'react-native'


export default function Matching({ route }) {
  const { triviaName, triviaItems } = route.params;

  const [selectedOptions, setSelectedOptions] = useState([]);
  const [selectedValues, setSelectedValues] = useState({});

  const handleOptionPress = (item, option) => {
    const updatedSelectedOptions = selectedOptions.map(triviaItem => {
      if (triviaItem.prompt === item.prompt) {
        const updatedAnswer = triviaItem.answer.map(answerOption => {
          if (answerOption.value === option.value) {
            return { ...answerOption, isSelected: true };
          } else if (answerOption.isSelected) {
            return { ...answerOption, isSelected: false };
          } else {
            return answerOption;
          }
        });
        return { ...triviaItem, answer: updatedAnswer };
      } else {
        return triviaItem;
      }
    });
    setSelectedOptions(updatedSelectedOptions);
  };

  const handleValuePress = (item, value) => {
    setSelectedValues((prevSelectedValues) => ({
      ...prevSelectedValues,
      [item.prompt]: value.value,
    }));
  };

  const handleCheckAnswers = () => {
    const isAllMatched = triviaItems.every(
      (item) =>
        item.answer &&
        item.answer.every(
          (option) =>
            selectedOptions[item.prompt] === option.option &&
            selectedValues[item.prompt] === option.value
        )
    );
    if (isAllMatched) {
      alert('All items are matched!');
    } else {
      alert('Not all items are matched.');
    }
  };

  const isOptionSelected = (prompt, option) => {
    const triviaItem = selectedOptions.find((item) => item.prompt === prompt);
    if (triviaItem) {
      const selectedOption = triviaItem.answer.find((answerOption) => answerOption.isSelected);
      return selectedOption && selectedOption.option === option;
    }
    return false;
  };
  const renderItem = ({ item }) => (
    <View>
      <Text>{item.prompt}</Text>
      {item.answer.map((option, index) => (
        <View key={index}>
          <Button
             onPress={() => handleOptionPress(item, option)}
             buttonStyle={{
               backgroundColor: isOptionSelected(item.prompt, option.option)
                 ? 'grey'
                 : 'blue',
             }}
          >
            {option.option}
          </Button>
          <Button
            onPress={() => handleValuePress(item, option)}
            buttonStyle={{
              backgroundColor:
                selectedValues[item.prompt] === option.value ? 'grey' : 'blue',
            }}
          >
            {option.value}
          </Button>
        </View>
      ))}
    </View>
  );

  return (
    <View>
      <Text>{triviaName}</Text>
      <FlatList
        data={triviaItems}
        renderItem={renderItem}
        keyExtractor={(item) => item.prompt}
      />
      <Button onPress={handleCheckAnswers}>
        <Text>Check Answers</Text>
      </Button>
    </View>
  );
}
