import React, { useState } from 'react'
import { Text, Button } from '@rneui/themed'
import { View, FlatList } from 'react-native'


export default function Matching ({ route }) {
  const { triviaName, triviaItems } = route.params;

  const updatedTriviaItems = triviaItems.map(item => {
    const updatedAnswer = item.answer.map(option => {
      return { ...option, isSelected: false };
    });
    return { ...item, answer: updatedAnswer };
  });

  const [selectedOptions, setSelectedOptions] = useState(updatedTriviaItems);

  const handleOptionPress = (item, option) => {
    const updatedSelectedOptions = selectedOptions.map((triviaItem) => {
      if (triviaItem.prompt === item.prompt) {
        const updatedAnswer = triviaItem.answer.map((answerOption) => {
          if (answerOption.value === option.value) {
            return { ...answerOption, isSelected: true };
          } else {
            return { ...answerOption, isSelected: false };
          }
        });
        return { ...triviaItem, answer: updatedAnswer, userAnswer: [option.value] };
      } else {
        return triviaItem;
      }
    });
    setSelectedOptions(updatedSelectedOptions);
  };

  const handleCheckAnswers = () => {
    const isAllMatched = triviaItems.every((item) =>
  item.answer && item.answer.every((option) =>
    selectedOptions.find((triviaItem) => triviaItem.prompt === item.prompt)
      .answer.find((answerOption) => answerOption.option === option.option)
      .isSelected
  )
);
    if (isAllMatched) {
      alert('All items are matched!');
    } else {
      alert('Not all items are matched.');
    }
  };

  const renderItem = ({ item }) => (
    <View>
      <Text>{item.prompt}</Text>
      {item.answer.map((option, index) => (
        <Button 
          key={index}
          onPress={() => handleOptionPress(item, option)} 
          style={{ backgroundColor: option.isSelected ? 'green' : 'white' }}
        >
          {option.option} - {option.value}
        </Button>
      ))}
    </View>
  );

  return (
    <View>
      <Text>{triviaName}</Text>
      <FlatList
        data={triviaItems}
        renderItem={renderItem}
        keyExtractor={(item) => item.prompt}
      />
      <Button onPress={handleCheckAnswers}>
        <Text>Check Answers</Text>
      </Button>
    </View>
  );
}
