import React, { useState } from 'react'
import { Text, Button } from '@rneui/themed'
import { View, FlatList, TouchableOpacity } from 'react-native'


export default function Matching({ route }) {
  const { triviaName, triviaItems } = route.params;

  const [matchedItems, setMatchedItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  const handleMatch = (option, value, index) => {
    let newSelectedItems = [...selectedItems];

    // If the user selects an item that is already selected, remove it from the array
    if (newSelectedItems.some((item) => item.index === index)) {
      newSelectedItems = newSelectedItems.filter((item) => item.index !== index);
    } else {
      newSelectedItems.push({ option, value, index });
    }

    setSelectedItems(newSelectedItems);
  };

  const handleCheckAnswers = () => {
    if (selectedItems.length !== triviaItems.length * 2) {
      Alert.alert('Error', 'Please match all items before checking your answers.');
      return;
    }

    const isAllCorrect = selectedItems.every(
      (item, index) =>
        selectedItems.some(
          (otherItem, otherIndex) =>
            otherIndex !== index &&
            otherItem.option === item.option &&
            otherItem.value === item.value
        )
    );

    const result = {
      matchedItems: selectedItems,
      isAllCorrect: isAllCorrect,
    };

    navigation.navigate('Summary', { result });
  };

  const renderItem = ({ item }) => (
    <View style={{ marginBottom: 16 }}>
      <Text style={{ fontSize: 20, fontWeight: 'bold', marginBottom: 8 }}>{item.prompt}</Text>
      <View style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
        <View style={{ flex: 1, marginRight: 8 }}>
          {item.answer.map((option, index) => (
            <TouchableOpacity
              key={`${item.prompt}_${option.option}`}
              style={{
                backgroundColor: selectedItems.some(
                  (item) => item.option === option.option
                )
                  ? 'yellow'
                  : matchedItems.some(
                      (matchedItem) =>
                        matchedItem.option === option.option &&
                        matchedItem.value === null
                    )
                  ? 'orange'
                  : 'white',
                borderWidth: 1,
                borderColor: 'black',
                padding: 16,
                marginBottom: 8,
                borderRadius: 8,
              }}
              onPress={() => handleMatch(option.option, null, index)}
            >
              <Text style={{ textAlign: 'center' }}>{option.option}</Text>
            </TouchableOpacity>
          ))}
        </View>
        <View style={{ flex: 1, marginLeft: 8 }}>
          {item.answer.map((value, index) => (
            <TouchableOpacity
              key={`${item.prompt}_${value.value}`}
              style={{
                backgroundColor:
                  selectedValue === value.value ? 'yellow' : matchedItems.find(
                      (matchedItem) =>
                        matchedItem.option === null &&
                        matchedItem.value === value.value
                    )
                  ? 'orange'
                  : 'white',
                borderWidth: 1,
                borderColor: 'black',
                padding: 16,
                marginBottom: 8,
                borderRadius: 8
              }}
              onPress={() => handleMatch(selectedOption, value.value)}>
              <Text style={{ textAlign: 'center' }}>{value.value}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </View>
    </View>
  );

  return (
    <View style={{ padding: 16 }}>
      <Text style={{ fontSize: 24, fontWeight: 'bold', marginBottom: 16 }}>{triviaName}</Text>
      <FlatList data={triviaItems} renderItem={renderItem} keyExtractor={(item) => item.prompt} />
      <View style={{ marginTop: 16 }}>
        <Button title="Check Answers" onPress={handleCheckAnswers} />
      </View>
    </View>
  );
}
