import React, { useState } from 'react';
import { View, Text } from 'react-native';
import { Dropdown } from 'react-native-material-dropdown';

export default function GrammarQuiz({ route }) {
  const { triviaName, triviaItems } = route.params;

  const [selectedAnswers, setSelectedAnswers] = useState({});

  const handleAnswerChange = (item, answer) => {
    setSelectedAnswers(prevSelectedAnswers => ({
      ...prevSelectedAnswers,
      [item.prompt]: answer.label,
    }));
  };

  return (
    <View>
      <Text>{triviaName}</Text>
      {triviaItems.map((item, index) => (
        <View key={index}>
          <Text>{item.prompt.replace('__option__', '')}</Text>
          <Dropdown
            label={item.prompt.match(/__option__(.*)__option__/)[1]}
            data={item.options}
            value={selectedAnswers[item.prompt]}
            onChangeText={(value) => handleAnswerChange(item, value)}
          />
        </View>
      ))}
    </View>
  );
}
