import React, { useState } from 'react'
import { Text, Button } from '@rneui/themed'
import { View } from 'react-native'


export default function Matching ({ route }) {
  const { triviaName, triviaItems } = route.params;
  const [matchedItems, setMatchedItems] = useState([]);

  const handleItemPress = (item) => {
    const updatedMatchedItems = [...matchedItems, item];
    setMatchedItems(updatedMatchedItems);
  };

  const renderItem = ({ item }) => (
    <Button onPress={() => handleItemPress(item)}>
      <View style={styles.item}>
        <Text style={styles.prompt}>{item.prompt}</Text>
        <Text style={styles.answer}>{item.answer}</Text>
      </View>
    </Button>
  );

  const renderSeparator = () => (
    <View style={styles.separator} />
  );

  const handleCheckAnswers = () => {
    const isAllMatched = triviaItems.every((item) => matchedItems.includes(item));
    if (isAllMatched) {
      alert('All items are matched!');
    } else {
      alert('Not all items are matched.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{triviaName} Matching</Text>
      <FlatList
        data={triviaItems}
        renderItem={renderItem}
        ItemSeparatorComponent={renderSeparator}
        keyExtractor={(item) => item.prompt}
      />
      <Button style={styles.button} onPress={handleCheckAnswers}>
        <Text style={styles.buttonText}>Check Answers</Text>
      </Button>
    </View>
  );
};
