import React from 'react'
import { Text, Input, Button } from '@rneui/themed'
import { View } from 'react-native';

export default function userInput ({route, navigation}) {
  const { triviaName, itemIndex } = route.params;
  const [userAnswer, setUserAnswer] = useState('');

  const triviaItem = triviaData.find(item => item.triviaName === triviaName).triviaItems[itemIndex];

  const handleSubmit = () => {
    if (userAnswer === triviaItem.correctAnswer) {
      navigation.navigate('Summary', { triviaName, itemIndex, isCorrect: true })
    } else {
      navigation.navigate('Summary', { triviaName, itemIndex, isCorrect: false })
    }
  }

  return (
    <View>
      <Text>{triviaItem.prompt}</Text>
      <Input
        value={userAnswer}
        onChangeText={setUserAnswer}
        keyboardType="numeric"
      />
      <Button
        title="Submit"
        onPress={handleSubmit}
      />
    </View>
  )
}
